import React, { createContext, useCallback, useState } from 'react';
import api from '../services/api';

interface AuthState {
    token: string;
    user: object; 
}

interface SignInCredentials {
    email  : string;
    password: string;
}

interface AuthContext {
    name: string;
    signIn(credentials: SignInCredentials): Promise<void>;
}

export const AuthContext = createContext<AuthContext>({} as AuthContext);

export const AuthProvider: React.FC = ({ children }) => {
    const [data, setData] = useState<AuthState>(() => {
        const token = localStorage.getItem('@GiBarber:tokne');
        const user = localStorage.getItem('@GiBarber:user');
    });

    const signIn = useCallback( async({email, password}) => { 
        const response = await api.post('sessions', {
            email,
            password,
        }); 

        const {token, user} = response.data;

        localStorage.setItem('@GiBarber:tokne', token);
        localStorage.setItem('@GiBarber:user', JSON.stringify(user));
    }, []);

    return (
        <AuthContext.Provider value={{name: 'Lincoln', signIn}}>
            {children}
        </AuthContext.Provider>
    );
}

export default AuthContext;
