import React, { useCallback } from 'react';
import { FiArrowLeft, FiMail, FiUser,FiLock } from 'react-icons/fi';
import { Form } from '@unform/web';
import * as Yup from 'yup';

import {Container, Content, Background} from './styles';

import logoImg from '../../assets/logo.svg';

import Input from '../../components/Input';
import Button from '../../components/Button';

const SignUp: React.FC = () => {
    const handleSubmit = useCallback(async (data: object) => {
        try {
            const schema = Yup.object().shape({
                name: Yup.string().required('Nome obrigatório'),
                email: Yup.string().required('E-mail obrigatório').email('Digite um e-mail válido'),
                password: Yup.string().required('Senha obrigatória').min(6, 'Senha deve conter minímo de 6 dígitos'),  
            });

            await schema.validate(data, {
                abortEarly: false,
            });

        } catch (err){
            console.log(err);
        }
    }, []);

    return (
    <Container>
        <Background />
        <Content>
            <img src={logoImg} alt="Go Barber"/>
            <Form onSubmit={handleSubmit}  >
                <h1>Faça seu cadastro</h1>
                <Input icon={FiUser} name="nome" placeholder="Nome" />
                <Input icon={FiMail} name="email" placeholder="E-mail" />
                <Input type="password" name="password" icon={FiLock} placeholder="Senha" />
                <Button type="submit">Cadastrar</Button>
            </Form>
            <a href=""><FiArrowLeft /> Voltar para logon</a>
        </Content>
    </Container>   
)};

export default SignUp;
