import styled, { css } from 'styled-components';

interface ContainerProps {
    isFocused: boolean;
    isFillded: boolean;
}

export const Container = styled.div<ContainerProps>`
    background: #232129;
    border-radius: 10px;

    border: 2px solid #232129;
    color: #666360;

    padding: 16px;
    width: 100%;

    display: flex;
    align-items: center;

    & + div {
        margin-top: 8px;
    }

    ${props => props.isFocused && css`
        color: #ff9000;
        border-color: #ff9000;
    `}

    ${props => props.isFillded && css`
        color: #ff9000;
    `}

 input{
    flex: 1;
    background: transparent;
    border: 0;
    color: #F4EDE8;

    &::placeholder{
        color: #666360;
    }
 }
    svg {
        margin-right: 16px;
    }

`;
