import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        outline: 0;
    }

    body{
        background: #312E38;
        color: #FFF;
        -webkit-font-smoothing: antialiased;
    }

    body, input, button {
        font-family: 'Roboto Slab', serif;
        font-size: 16px;
    }

    h1, h2, h3, h4, h5, h6, strong{
        font-weight: 500;
    }

    button{
        cursor: pointer;
    }

    input{
            background: #232129;
            border-radius: 10px;
            border: 2px solid #232129;
            color: #F4EDE8;
            padding: 16px;
            width: 100%;

            &::placeholder{
                color: #666360;
            }

            & + input {
                margin-top: 8px;
            }
        }

        button {
            background: #FF9000;
            height: 56px;
            border-radius: 10px;
            border: 0;
            color: #312e38;
            padding: 0 16px;
            width: 100%;
            margin-top: 16px;
            font-weight: 500;
            transition: backgound-color 0.2s;

            &:hover{
                background: ${shade(0.2, '#FF9000')};
            }
        }
`;
